var localUserLoaded, localUserReq;
OF.GA.setAccount('UA-3107048-21', 'spotlight.android.openfeint.com');
$.loadScript('dashboard/dashboard');
if ((typeof jasmine !== "undefined" && jasmine !== null) && OF.isBrowser) {
  $.loadCss('settings/spec');
}
localUserLoaded = localUserReq = null;
OF.loadLocalUser = function() {
  if (OF.user.isLoaded) {
    return typeof localUserLoaded === "function" ? localUserLoaded() : void 0;
  } else {
    return localUserReq || (localUserReq = OF.api("/xp/users/me", {
      success: function(userData) {
        OF.user = userData.user;
        OF.user.isLoaded = true;
        if (typeof localUserLoaded === "function") {
          localUserLoaded();
        }
        return localUserLoaded = localUserReq = null;
      }
    }));
  }
};
OF.localUserLoaded = function(callback) {
  if (OF.user.isLoaded) {
    return callback();
  } else {
    return localUserLoaded = callback;
  }
};

init:
//load common js for dashboard webview
$.loadScript('dashboard/dashboard_model');

OF.loadLocalUser();
$('body').addClass('textured');
$('.button').pretouch();

// Menu handling functions
if (OF.action.isSupported('enableMenuItems')) {
  OF.sendAction('enableMenuItems', {'items':['home','settings','exit']});
}

if (!OF.menu) {
  OF.menu = function(buttonName) {
    if (typeof OF.menu[buttonName] === 'function')
    {
      OF.menu[buttonName]();
    }
    else
    {
      OF.log('Unknown menu button hit: '+ buttonName);
    }
  };
}

OF.menu.home = function() {
  if (OF.navigationStack[0].url.match(/dashboard\/user/)) {
    OF.goBack({root:true});
  } else {

    // This is a bit of a hack for now...
    OF.navigateToUrl("dashboard/user", {
      complete: function() {
        OF.pages.unshift(OF.pages.pop());
        OF.action('back', {root:true}, OF.contentLoaded);
      }
    });
  }
};

OF.menu.settings = function() {
  OF.sendAction('openSettings');
};

OF.menu.exit = function() {
  OF.sendAction('dismiss');
};

// Base friends page functions
if (!OF.friendsList) {
  OF.friendsList = {
    init: function() {
      jQuery.lazyLoad({
        threshold: 0.5,  //percentage of the window height left to scroll that will trigger the next page
        nextPageLoaded: function(page, data){ 
          if (page == 1) {
            OF.friendsList.renderFriends(data);                 
          } else {
            OF.friendsList.addFriends(data);         
          }
        },
        nextPageShouldLoad: function(page, callback) {
          OF.api('/xp/friends', {
            params: {
              user_id:   OF.page.user.id,
              page:      page,
              page_size: 50
            },
            success: function(data) {
              callback(data.users, data.users.length < 50)
            },
            failure: function() { callback([]); }
          });
        },
        isEmpty: function() { OF.friendsList.renderFriends([]); },
        isFinished: function() { jQuery("#loading").hide(); }
      });
    },

    renderFriendItems: function(friends) {
      var html = jQuery.map(friends, function(friend) {
       return tmpl('user_tmpl', friend);
      });
      return html;
    },

    renderFriends : function(friends) {    
      var html = OF.friendsList.renderFriendItems(friends);

      if (html.length > 0) {
        jQuery('#friends_list > ul').html(html.join('')).unhide();
        jQuery("#friends_list .no_data").hide();
      } else {
        jQuery('#friends_list > ul').hide();
        jQuery("#friends_list .no_data").unhide();      
      }
    },

    addFriends : function(friends) {    
      var html = OF.friendsList.renderFriendItems(friends);
      jQuery('#friends_list > ul').append(html.join(''));
    }
  };
}

// Go home on touching the logo area
$('#home_button').touch(OF.menu.home);
$.loadCss('dashboard/games');
$.loadScript('dashboard/games');
window.gamesPage = new GamesPage;
